import { Router } from 'express';
import RoomController from './controller';


const roomRouter = Router();

roomRouter.get('/', RoomController.getAllRooms);
roomRouter.post('/', RoomController.addRoom);
roomRouter.get('/:id', RoomController.getARoom);
roomRouter.put('/:id', RoomController.updatedRoom);
roomRouter.delete('/:id', RoomController.deleteRoom);

export default roomRouter;

import RoomService from './service';
import Util from '../utils/util';

const util = new Util();

class RoomController {
  static async getAllRooms(req, res) {
    try {
      const allRooms = await RoomService.getAllRooms();
      if (allRooms.length > 0) {
        util.setSuccess(200, 'Rooms retrieved', allRooms);
      } else {
        util.setSuccess(200, 'No Room found');
      }
      return util.send(res);
    } catch (error) {
      util.setError(400, error);
      return util.send(res);
    }
  }

  static async addRoom(req, res) {
    if (!req.body.type || !req.body.guest_type || !req.body.number_of_beds || !req.body.price) {
      util.setError(400, 'Please provide complete details');
      return util.send(res);
    }
    const newRoom = req.body;
    try {
      const createdRoom = await RoomService.addRoom(newRoom);
      util.setSuccess(201, 'Room Added!', createdRoom);
      return util.send(res);
    } catch (error) {
      util.setError(400, error.message);
      return util.send(res);
    }
  }

  static async updatedRoom(req, res) {
    const alteredRoom = req.body;
    const { id } = req.params;
    if (!Number(id)) {
      util.setError(400, 'Please input a valid numeric value');
      return util.send(res);
    }
    try {
      const updatedRoom = await RoomService.updateRoom(id, alteredRoom);
      if (!updatedRoom) {
        util.setError(404, `Cannot find room with the id: ${id}`);
      } else {
        util.setSuccess(200, 'Room updated', updatedRoom);
      }
      return util.send(res);
    } catch (error) {
      console.log('ERROR', error);
      util.setError(404, error);
      return util.send(res);
    }
  }

  static async getARoom(req, res) {
    const { id } = req.params;

    if (!Number(id)) {
      util.setError(400, 'Please input a valid numeric value');
      return util.send(res);
    }

    try {
      const room = await RoomService.getARoom(id);

      if (!room) {
        util.setError(404, `Cannot find room with the id ${id}`);
      } else {
        util.setSuccess(200, 'Found Room', room);
      }
      return util.send(res);
    } catch (error) {
      console.log('ERROR', error);
      util.setError(404, error);
      return util.send(res);
    }
  }

  static async deleteRoom(req, res) {
    const { id } = req.params;

    if (!Number(id)) {
      util.setError(400, 'Please provide a numeric value');
      return util.send(res);
    }

    try {
      const roomToDelete = await RoomService.deleteRoom(id);

      if (roomToDelete) {
        util.setSuccess(200, 'Book deleted');
      } else {
        util.setError(404, `Room with the id ${id} cannot be found`);
      }
      return util.send(res);
    } catch (error) {
      util.setError(400, error);
      return util.send(res);
    }
  }
}

export default RoomController;

import database from '../../database/models';

class RoomService {
  static async getAllRooms() {
    try {
      return await database.Room.findAll();
    } catch (error) {
      throw error;
    }
  }

  static async addRoom(newRoom) {
    try {
      return await database.Room.create(newRoom);
    } catch (error) {
      throw error;
    }
  }

  static async updateRoom(id, updateRoom) {
    try {
      const roomToUpdate = await database.Room.findOne({
        where: { id: Number(id) }
      });

      if (roomToUpdate) {
        await database.Room.update(updateRoom, { where: { id: Number(id) } });

        return updateRoom;
      }
      return null;
    } catch (error) {
      throw error;
    }
  }

  static async getARoom(id) {
    try {
      const room = await database.Room.findOne({
        where: { id: Number(id) }
      });

      return room;
    } catch (error) {
      throw error;
    }
  }

  static async deleteRoom(id) {
    try {
      const roomToDelete = await database.Room.findOne({ where: { id: Number(id) } });

      if (roomToDelete) {
        const deletedRoom = await database.Room.destroy({
          where: { id: Number(id) }
        });
        return deletedRoom;
      }
      return null;
    } catch (error) {
      throw error;
    }
  }
}

export default RoomService;

'use strict';
import fs from 'fs';
import path from 'path';
import Sequelize from 'sequelize';
import configSetting from '../config/config';

const basename = path.basename(__filename);
const env = process.env.NODE_ENV || 'development';
const config = configSetting[env];

const db = {};

let sequelize;
if (config.url) {
  sequelize = new Sequelize(config.url, config);
} else {
  sequelize = new Sequelize(config.database, config.username, config.password, config);
}

fs
  .readdirSync(__dirname)
  .filter(file => {
    return (file.indexOf('.') !== 0) && (file !== basename) && (file.slice(-3) === '.js');
  })
  .forEach(file => {
    const model = sequelize['import'](path.join(__dirname, file));
    db[model.name] = model;
  });

Object.keys(db).forEach(modelName => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

export default db;

